class FPS:
    """Frames Per Second for OpenCV Videos.
    Code is courtesy of https://github.com/jrosebr1/imutils, with a few minor changes.
    """

    def __init__(self):
        # store the start time, end time, and total number of frames
        # that were examined between the start and end intervals
        self._start = None
        self._end = None
        self._current = None
        self._numFrames = 0

    def start(self):
        # start the timer
        self._start = datetime.datetime.now()
        return self

    def stop(self):
        # stop the timer
        self._end = datetime.datetime.now()

    def update(self):
        # increment the total number of frames examined during the
        # start and end intervals
        self._numFrames += 1
        self._current = datetime.datetime.now()

    @property
    def elapsed(self):
        # return the total number of seconds between the start and
        # end interval
        if self._end is None:
            if self._current is None:
                return 0
            else:
                return round((self._current - self._start).total_seconds())
        else:
            return round((self._end - self._start).total_seconds())

    @property
    def str_elapsed(self):
        try:
            mins, secs = divmod(round((self._end - self._start).total_seconds()), 60)
            return '{:02d}:{:02d}'.format(mins, secs)
        except:
            return

    @property
    def fps(self):
        # compute the (approximate) frames per second
        try:
            return round(self._numFrames / self.elapsed)
        except ZeroDivisionError:
            return

fps = FPS()


